mapper.select:
SELECT P.UID, P.USERUID, P.TITLE, P.CONTENT, P.CATEGORY, P.UPDATEDATE
FROM POST P
WHERE P.USERUID = #{userUid}


        AND P.CATEGORY = #{category}


ORDER BY UPDATEDATE DESC

SELECT P.UID, P.USERUID, P.TITLE, P.CONTENT, P.CATEGORY, P.UPDATEDATE
FROM POST P
WHERE P.UID = #{uid}

mapper.update:
UPDATE POST
SET TITLE = #{title}, CONTENT = #{content}, CATEGORY = #{category}, UPDATEBY = #{updateBy}, UPDATEDATE = SYSDATE()
WHERE UID = #{uid}
